INSERT INTO `tbl_baiviet` (`baiviet_id`, `tenbaiviet`, `tomtat`, `noidung`, `danhmuctin_id`, `baiviet_image`) VALUES
(7, 'Lễ công bố hợp tác giữa Thiên Long và PEGA: Chung tay lan tỏa tri thức', 'Vào ngày 7/10/2022, tại Hội sách Hà Nội 2022, “Lễ công bố hợp tác giữa Thiên Long và PEGA: Chung tay lan tỏa tri thức” đã diễn ra với sự tham gia của lãnh đạo hai công ty, lãnh đạo Cục xuất bản, các đối tác và cơ quan truyền thông.', '<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"color:#000000;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Lễ công bố này đánh dấu sự kiện một công ty văn phòng phẩm và một công ty giàu tiềm cùng năng hợp tác để lan tỏa và kiến tạo tri thức Việt. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"color:#000000;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\">Theo thông tin tại Lễ công bố, PEGA cam kết đồng hành cùng với các công ty sách, các nhà xuất bản ở Việt Nam để tạo ra các sản phẩm tri thức hữu ích, kịp thời đáp ứng nhu cầu học tập và nâng cao tri thức cho người Việt Nam. Trong đó, đồng hành cùng Zenbooks và Saigon Books là bước đi đầu tiên của PEGA trong hành trình chinh phục sứ mệnh của mình.</span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 200px; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"color:#000000;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"border: 1pt solid rgb(238, 238, 238); padding: 0cm;\">.</span></span><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin1_1_1.jpg\" style=\"width: 675px; height: 450px;\" /></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"color:#000000;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Sứ mệnh của PEGA là mang nguồn tri thức phong phú của thế giới đến với người Việt, cũng như trở thành cầu nối đưa tri thức của người Việt đến với bạn bè quốc tế, tạo điều kiện lan tỏa rộng rãi tri thức của người Việt Nam, phục vụ nhu cầu nâng cao kiến thức của dân tộc.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"color:#000000;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Trong thực tế, Thiên Long và PEGA nhận thấy rằng còn rất nhiều người và rất nhiều nơi trên đất nước của mình vẫn chưa có nhiều điều kiện để tiếp cận tri thức thông qua những cuốn sách hay, hay rất nhiều trẻ em chưa có thói quen đọc sách. Vì vậy, Thiên Long và PEGA hợp tác để trợ lực lẫn nhau, cùng hành động nhiều hơn, thiết thực hơn để góp phần cùng với các NXB, các công ty sách đưa sách đến với người đọc, người học ngày càng tốt hơn, hữu ích hơn. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 200px; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"color:#000000;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"border: 1pt solid rgb(238, 238, 238); padding: 0cm;\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin1_1_2.jpg\" style=\"width: 674px; height: 450px;\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"color:#000000;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Chia sẻ về kỳ vọng khi hợp tác với Thiên Long, ông Nguyễn Tuấn Quỳnh - Chủ tịch HĐQT PEGA cho biết “Chúng tôi và Tập đoàn Thiên Long đã có quá trình hợp tác, tìm hiểu về nhau khá lâu trước khi quyết định cùng chung tay thực hiện sứ mệnh lan tỏa tri thức. Với sự đồng hành của Tập đoàn Thiên Long, PEGA sẽ hội đủ nguồn lực để trở thành đối tác quan trọng của các nhà xuất bản lớn trên thế giới; đầu tư bài bản và có chiều sâu cho mảng sách giáo khoa, giáo trình, sách tham khảo; phát triển những sản phẩm mới và hoàn thiện hệ sinh thái tri thức mà hai bên đang triển khai”.</span></span></span></span></span></p>\r\n', 3, 'tin1.1.jpg'),
(8, 'Ba bí mật giúp cuộc sống của bạn trở nên đơn giản hơn', 'Chúng ta hay cảm thán “đời là bể khổ” hay “cuộc sống thật khó khăn”, nhưng có một người phụ nữ lại dám khẳng định ngược lại: cuộc sống thật sự rất đơn giản.', '<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Chân dung một người người phụ nữ quyền lực</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Là nhà chiến lược toàn cầu và nhà dự đoán kinh tế, Chin-Ning Chu là một cá nhân xuất sắc với xuất thân khiêm tốn. Bà sở hữu thứ năng khiếu hiếm có nhất của con người: Khả năng lý giải những chân lý vĩnh hằng, phức tạp bằng những ẩn dụ và minh họa thật dễ dàng, đầy ý nghĩa. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Đồng thời, Chin-Ning Chu là tác giả có nhiều cuốn sách bán chạy nhất ở châu Á và khu vực vành đai Thái Bình Dương. Bà nguyên là Chủ tịch Công ty Marketing châu Á và Viện Nghiên cứu Chiến lược, và là nhà chiến lược kinh doanh hàng đầu tại Mỹ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 320px; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin2_1_1.jpg\" style=\"width: 450px; height: 450px;\" /></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Độc giả đã từng thưởng thức những triết lý sống đơn giản nhưng vô cùng sâu sắc của Chin-Ning Chu qua hai cuốn sách: Mặt dày tâm đen (cách phát huy những tính cách, tài năng độc đáo của bản thân để đạt được điều mình mong muốn) và Binh pháp Tôn tử dành cho phái đẹp (một tác phẩm sẽ hữu ích cho phụ nữ hiện đại, nhất là khi đối diện với tình huống khó khăn trong cuộc sống cũng như trong công việc).</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">1. Bí mật thứ nhất: Làm thế nào để quản lý thời gian?</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Một trong những điều mà tôi thích trong cuốn sách là bí mật đầu tiên - Điều tiết hành động của bạn trong việc quản lý thời gian. Lời than vãn “không có thời gian” ta vẫn hay nghe thấy thường xuất phát từ việc một người đã để cho những người xung quanh cướp mất thì giờ của mình. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Trong công việc, đồng nghiệp thường hay ghé ngang chỗ bạn để tán gẫu, những cuộc gọi điện thoại bất ngờ, khách khứa tiện thể ghé thăm. Dường như, những nguyên nhân không thể kiểm soát như thế đã cướp mất thì giờ của bạn và biến bạn thành nạn nhân.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 200px; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin2_1_2.jpg\" style=\"width: 621px; height: 450px;\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Để hoàn toàn tập trung vào việc giải quyết những công việc và tránh rơi vào tình trạng bị người khác quấy rầy, bạn cần tinh tế điều chỉnh giọng điệu của mình để đối phương hiểu bản thân không muốn bị quấy rầy, hoặc bảo người ấy rút ngắn cuộc hội thoại lại và bạn sẽ trò chuyện lai sau vào lúc thích hợp hơn. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Hoặc trước khi gọi điện thoại, bạn có thể viết ra những điểm chính và mục đích mà bạn muốn truyền đạt đến đối phương… Đó chỉ là một trong số nhiều thủ thuật nhỏ trong cách điều tiết thói quen sử dụng thời gian có chủ đích mà Làm ít được nhiều hướng đến.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">2. Bí mật thứ hai: Làm sao để tâm lý thanh thản?</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Ở phần bí mật thứ hai - Giữ tâm trí thanh thản, tác giả chia sẻ rằng, cuộc sống có quá nhiều nỗi lo toan, cơm, áo, gạo, tiền, giá trị thành công và hạnh phúc. Ngoài ra, chúng ta bị làm phiền không chỉ bởi quá khứ đau buồn mà còn bởi sự căng thẳng của hiện tại.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 200px; text-align: justify;\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin2_1_3.jpg\" style=\"width: 720px; height: 450px;\" /></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span new=\"\" roman=\"\" style=\"font-family: \" times=\"\">Hãy lập ra một danh sách mô tả cuộc sống của bạn sẽ ra sao vào năm tới. Rồi làm một danh sách khác mô tả cuộc sống sẽ ra sao nếu bạn vẫn tiếp tục mọi việc như bình thường. So sánh hai danh sách với nhau. rồi tự hỏi, điều gì làm cho hai danh sách khác biệt nhau?</span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Câu trả lời có thể liên quan đến nỗi sợ hãi. Chính bởi lo sợ chuốc lấy những hậu quả của những việc làm lầm lỡ, những quyết định sai trái, bạn hủy hoại sự tồn tài của bản thân và sự nghiệp. Thế nên, bạn đành chấp nhận gánh vác công việc và cuộc sống với tinh thần “vẫn làm việc như bình thường”. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">3. Bí mật thứ ba: Kết nối với năng lực thần thánh</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Từ những gợi ý trên, một thắc mắc được đặt ra, “làm sao để chúng ta biết khi nào phải dũng cảm hành động và khi nào chúng ta dừng lại để thiền định - giữ tâm trí bình an? Câu trả lời: Thiền định rèn luyện bạn ở yên trong hiện tại, giúp bạn sống và làm việc hiệu quả hơn</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 240px; text-align: justify;\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin2_1_4.jpg\" style=\"width: 600px; height: 450px;\" /></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Trong cuốn sách Làm ít được nhiều, tác giả Chin-Ning Chua nhà chiến lược toàn cầu giàu kinh nghiệm, sẽ cho chúng ta một cái nhìn toàn diện về con người, và khả năng tiềm ẩn trong chúng ta chưa được khai thác thấu triệt để đạt kết quả: Làm ít nhưng được nhiều.</span></span></span></span></span></p>\r\n', 2, 'tin2.1.jpg'),
(9, '100 cách sống hạnh phúc: Xây dựng sức mạnh nội tại từ những việc đơn giản nhất', '“Hạnh phúc cần năng lượng và mất nước dẫn đến hôn mê. Vì vậy, chỉ cần chí ít một phần nào đó, hiển nhiên, hạnh phúc cũng đòi hỏi đủ nước…”, đó là lời chia sẻ của Dr. Timothy J.Sharp, trong quyển sách “100 cách sống hạnh phúc”.', '<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Vì sao bạn phải uống nước?</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-family: &quot;Times New Roman&quot;, Times, serif; font-size: 20px;\">Nước là “thần dược” giúp duy trì sức khỏe và vẻ đẹp. Nước chiếm 60% cơ thể. Nước chiếm trên 70% tổng khối lượng các cơ quan như não, tim, phổi, thận, 65% khối lượng da, và ngay cả xương cũng có khoảng 30% là nước.</span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 200px; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin2_2_1.jpg\" style=\"width: 650px; height: 450px;\" /></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Ở cấp độ tế bào, nước giúp vận chuyển khí oxi, chất dinh dưỡng và duy trì cân bằng ion, đóng vai trò như dung môi của các phản ứng sinh hóa của tế bào. Ở cấp độ cơ thể, nước hỗ trợ tiêu hóa, đào thải các chất cặn và vi khuẩn khỏi bàng quang, bảo vệ cơ quan và mô bằng cách cung cấp độ ẩm cho lớp màng nhầy, duy trì huyết áp, điều hòa thân nhiệt, làm chất bôi trơn cho khớp cũng như làm đệm cho hệ thần kinh và não.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Uống đủ nước giúp chúng ta hạnh phúc</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Mặc dù vẫn còn một số tranh cãi về lượng nước cần uống mỗi ngày, các cơ quan y tế thường khuyên chúng ta uống khoảng 8 đến 14 ly nước mỗi ngày. Tùy vào khối lượng bài tập thể dục, khí hậu mà bạn sinh sống, tuổi tác và tình trạng sức khỏe mà lượng nước chính xác nạp vào cơ thể khác nhau. Tuy nhiên, tất cả chúng ta nên uống tối thiểu 8 ly nước mỗi ngày và nói một cách đơn giản là hãy nhớ cứ hơn 1 giờ uống khoảng 1 ly nước.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Ngoài uống đủ nước, còn cách nào để hạnh phúc?</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Hạnh phúc thật ra rất dễ dàng và đơn giản, đến từ những việc làm nhỏ hằng ngày như uống nước. Ngoài uống nước, bạn còn đến 99 cách khác có thể áp dụng ngay và luôn để hạnh phúc, như: Tập thể dục để nâng cao tinh thần, ăn vặt lành mạnh, giãn cơ, lắng nghe cơ thể, ngủ đủ giấc…</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">100 cách sống hạnh phúc là cuộc hành trình xoay quanh hạnh phúc hay sự mãn nguyện chủ quan, từ đó, tác giả nhìn nhận các khái niệm này dưới nhiều góc độ: Từ lý tính như khoa học não bộ cho đến cảm xúc tinh thần trong các mối quan hệ… Đây là cuốn sách giúp bạn xây dựng niềm vui từ trong nội tại.</span></span></span></span></span></p>\r\n', 2, 'tin2.2.jpg'),
(10, 'Hai quyển sách kỹ năng sống dành tặng phái đẹp', 'Phái đẹp đều mong muốn mình có được cuộc sống hạnh phúc nhất. Thế nhưng, thực tế có bao nhiêu người đạt được như ý nguyện? Bạn khoan nói rằng họ may mắn, thực ra đôi khi họ không tài giỏi và xinh đẹp hơn bạn đâu? Mà bởi vì, họ có trang bị cho mình những kỹ năng sống thực sự hữu ích để dễ dàng chạm tới những ước mơ ấy.  Vậy nó bao gồm những kỹ năng gì? ', '<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Hai quyển sách về kỹ năng sống: Sự trỗi dậy của phái đẹp (Sally Helgesen và Marshall Goldsmith) và Binh pháp Tôn tử dành cho phái đẹp (Chin-ning Chu) dành riêng cho phụ nữ sẽ bật mí cho bạn những tips kỹ năng để ứng biến hiệu quả trong công việc lẫn cuộc sống hiện đại!</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Sách về kỹ năng sống quan trọng trong cuộc sống chúng ta</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Chính vì nguyên do trên mà những cuốn sách về kỹ năng sống đã xuất hiện. Sách về vấn đề này dành cho tất cả mọi người đặc biệt là phái đẹp. Họ vốn mong manh, yếu đuối. Họ cần trang bị những kỹ năng sống này để có thể sống tốt đẹp và hạnh phúc hơn.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Kỹ năng sống trong Binh pháp Tôn tử dành cho phái đẹp (Chin-Ning Chu)</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Binh pháp Tôn tử dành cho phái đẹp - Cuốn sách về kỹ năng sống dành cho phái đẹp của Chin-Ning Chu. Nội dung của sách là những câu chuyện và lời tâm tình của tác giả xoay quanh những cách làm cho phụ nữ trở nên thành công, hạnh phúc mà tránh những thương tổn, đối đầu không cần thiết.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Điểm cộng là sách dựa vào những chiến thuật của Tôn Tử. Một quân sư tài ba của Trung Hoa cuối thời Xuân Thu. Tuy nhiên, với sự nhìn nhận của mình, tác giả dựa vào nó và cải biến cho phù hợp với phái nữ hiện nay.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Binh pháp Tôn Tử dành cho phái đẹp giúp những bóng hồng nhận ra tám điểm mới về mình như phụ nữ sáng tạo ra cái đẹp, phụ nữ không đầu hàng, phụ nữ là người bảo vệ mẹ Trái Đất… Để từ tám điểm mới ấy, phái đẹp trở nên tự tin vào chính mình hơn.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 200px; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin2_3_1.jpg\" style=\"width: 761px; height: 450px;\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><strong><span style=\"line-height:107%\"><span style=\"line-height:107%\">Kỹ năng sống trong Sự trỗi dậy của phái đẹp (Sally Helgesen và Marshall Goldsmith)</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Trong Sự trỗi dậy của phái đẹp, tác giả chỉ cho ta cách để nhìn nhận vấn đề theo hướng tích cực&nbsp; khi gặp thất bại. Cả hai tác giả khuyên ta không nên&nbsp; đổ lỗi cho người khác mà hãy dành thời gian nhìn nhận lại chính mình. Và từ đó, tự thân tìm cách giải quyết phù hợp nhất. Đây là một kỹ năng sống tuy đơn giản nhưng rất khó để thực hành.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"color:#000000;\"><span new=\"\" roman=\"\" times=\"\">Đồng thời, họ cung cấp cho ta những cách để gạt bỏ 12 thói quen tai hại, ăn sâu vào nếp nghĩ của phái đẹp như ngại nhận thành tích bản thân, cố làm hài lòng người khác, thái quá vấn đề… Những thói quen này là những vật cản chắn bước đường thành công của những bóng hồng.</span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 200px; text-align: justify;\"><span new=\"\" roman=\"\" style=\"font-family: \" times=\"\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin2_3_2.jpg\" style=\"width: 742px; height: 450px;\" /></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Hai quyển sách về kỹ năng sống ở trên sẽ là một trong những cuốn cẩm nang cần thiết cho phái đẹp. Bởi sách giúp cho họ trang bị, bổ sung những kiến thức về kỹ năng sống cho chính mình. Để từ đó, phái đẹp dễ dàng gặt hái thành công trong đời sống tình cảm lẫn công việc. </span></span></span></span></span></p>\r\n', 2, 'tin2.3.png'),
(11, 'Phát hành sách trực tuyến tăng trưởng mạnh', 'Hơn một năm qua, dịch bệnh Covid-19 đã có những tác động lớn tới ngành sách. Lĩnh vực phát hành có sự thay đổi giữa bán sách trực tiếp và phát hành sách trực tuyến.\r\n\r\n', '<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\">Dịch bệnh đặt ra rất nhiều khó khăn và thách thức với các nhà sách thực địa. Mọi người hạn chế tiếp xúc khiến việc mua sách trực tiếp giảm. Khi địa phương, khu vực có yêu cầu giãn cách xã hội, nhà sách trong khu vực đó cũng phải tạm ngưng hoạt động.</span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">“Đến nay, dịch bệnh kéo dài hơn một năm, một số người bắt đầu lo lắng, tình hình tài chính chung không được thoải mái như mùa dịch năm ngoái nữa. Do vậy, người ta quan tâm đến mặt hàng thiết yếu. Việc phát hành sách trở nên khó khăn hơn”, ông Minh nói.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Bà Trần Nhật Hoàng Phương - Trưởng phòng Marketing, Công ty Phương Nam - cho biết sự thay đổi về nhu cầu của khách hàng, sự khan hiếm nguyên vật liệu trọng yếu như giấy dẫn đến giá hàng hóa tăng mạnh; vận chuyển quốc tế chưa hoàn toàn lưu thông... Đó là những nguyên nhân khiến ngành sách gặp khó.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Trái ngược với hiệu sách thực địa, các đơn vị bán sách online đạt tăng trưởng trong đại dịch. Thương mại điện tử vốn phát triển trong những năm qua, từ khi có dịch bệnh, mọi người có xu hướng mua sắm trực tuyến nhiều hơn. Ngành sách không phải ngoại lệ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Ông Đỗ Huy Quốc - Trưởng ngành hàng sách tại Tiki - cho biết trong các năm vừa qua, ngành sách của trang thương mại điện tử này đều đạt được mục tiêu tăng trưởng ổn định. Quý I năm nay, ngành sách của Tiki tăng trưởng gấp hai lần so với quý IV năm 2020, vốn là mùa mua sắm cuối năm sôi động.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 200px; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin1_2_1.jpg\" style=\"width: 675px; height: 450px;\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Trước tình hình dịch Covid-19 đang có diễn biến phức tạp, nhiều công ty phát hành sách, doanh nghiệp lựa chọn đẩy mạnh phân phối qua những kênh online. Đây là cơ hội thúc đẩy ngành sách trên các sàn thương mại điện tử.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Nhiều đơn vị, cá nhân đã tài trợ vận chuyển, tài trợ trực tiếp trên từng cuốn sách cho độc giả như: Tổng công ty Bưu điện Việt Nam VNPost, Công ty cổ phần dược phẩm Thái Minh Thương hiệu tư vấn quản lý Vitranet24, Tập đoàn Bảo Việt, Công ty cổ phần thương mại dịch vụ GNC, Công ty cổ phần phát triển giải pháp giáo dục ViVi Education, Quỹ từ thiện Ngân Hà Hải Phòng, các nhà mạng Viettel, VinaPhone, MobiFone</span></span></span></span></span></p>\r\n', 3, 'tin1.2.jpg'),
(12, 'Giới xuất bản bất ngờ với lượng sách bán trên BH Bookstore.', 'Các NXB, công ty phát hành cho biết lượng người mua tăng cao, Hội sách trực tuyến quốc gia đang có bước đi đúng, đáp ứng nhu cầu người mua và người làm sách.', '<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Hội sách trực tuyến quốc gia lần thứ 2 đang diễn ra những ngày cuối trên sàn BH Bookstorevới nhiều hoạt động sôi nổi. Nếu năm 2020, hội sách được thực hiện trong tình thế giãn cách vì dịch, thì năm nay, chương trình đã có một bước tập dượt để giới xuất bản có thể làm quen hơn với chuyển đổi số.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><strong><span new=\"\" roman=\"\" times=\"\">Lượng sách bán tăng</span></strong></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Năm ngoái, Hội sách trực tuyến quốc gia ghi nhận lượng sách lý luận chính trị, sách lịch sử được lượng lớn bạn đọc quan tâm. Năm nay, ưu thế này tiếp tục được phát huy. Tham gia hội sách, NXB Chính trị quốc gia Sự thật - đơn vị xuất bản sách lý luận chính trị lớn nhất cả nước - không đặt nặng vấn đề lợi nhuận mà mong muốn qua sự kiện này, cùng các các đơn vị tham gia sẽ góp phần lan tỏa hơn nữa văn hóa đọc. Nhà xuất bản đã nhận được nhiều đơn đặt hàng, phản hồi tốt từ độc giả.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Hội sách diễn ra trên sàn book365.vn của NXB Thông tin và Truyền thông. Ông Đàm Quốc Cường - đại diện NXB Thông tin và Truyền thông - cho biết lượng sách bán trong thời gian có hội sách lớn hơn nhiều so với ngày thường.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">“Hội sách là thời điểm mà ban tổ chức tập trung thực hiện nhiều biện pháp nhằm khuyến khích bạn đọc đến với sách vở. Nhiều hoạt động về văn hóa đọc được thực hiện song song với các chương trình thúc đẩy bán hàng. Bản thân các gian hàng cũng tự chiết khấu, khuyến mãi. Đó là một phần tạo nên sức hút cho hội sách”, đại diện NXB Thông tin và Truyền thông lý giải sức hút của hội sách.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt 200px; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\"><img alt=\"\" src=\"http://localhost/WebBanSach/uploads/images/tin1_3_1.jpg\" style=\"width: 660px; height: 440px;\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><strong><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Sàn thương mại với sách là cốt lõi</span></span></span></span></span></strong></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Có được kết quả khả quan như vậy là nhờ Hội sách trực tuyến quốc gia đã tạo ra được những điểm khác biệt. Về khách quan, trong điều kiện dịch bệnh Covid-19 vẫn diễn biến khó lường, công chúng hạn chế tiếp xúc, vì vậy kênh thương mại điện tử được cho là phù hợp.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Mặt khác, hội sách đã chủ động thực hiện nhiều chương trình hấp dẫn, lấy quyền lợi bạn đọc làm trọng tâm. Giới xuất bản đã đánh giá tích cực về ý nghĩa của hội sách.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"color:#000000;\"><span style=\"font-size:20px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Ông Hoàng Mạnh Thắng - Giám đốc Trung tâm Thông tin và Nghiên cứu khoa học, NXB Chính trị quốc gia Sự thật - nói trước những thách thức của đại dịch hiện nay, việc tổ chức hội sách trực tuyến “mở ra những cơ hội chưa từng có cho những người làm xuất bản, từ phía các cơ quan quản lý, các đơn vị kinh doanh, xuất bản sách cho đến độc giả”.</span></span></span></span></span></p>\r\n', 3, 'tin1.3.jpg');

